    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        long montpag, cantper, op1, iva=16, conversion, precioper=25000, resultado;
        String res, conv1, operacion;
        
        if (txtNumPer.getText().trim().isEmpty())
        {
            JOptionPane.showMessageDialog(this, "Ingrese Numero de Personas", "ERROR", JOptionPane.ERROR_MESSAGE);
            txtNumPer.requestFocusInWindow();
        }
        else {      
        cantper= Long.parseLong(txtNumPer.getText());
           
        op1= cantper*precioper;
        conversion= (op1*iva)/100;
        resultado= op1+conversion;
        
        operacion= String.valueOf(op1);
        conv1= String.valueOf(conversion);
        res= String.valueOf(resultado);
        txtMontPag.setText(res);
        txtMontAgreg.setText(conv1);
        txtCantSinIva.setText(operacion);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

