    private void txtNumPerKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtNumPerKeyTyped
        // TODO add your handling code here:
          char c=evt.getKeyChar(); 
          if(!Character.isDigit(c)) 
          { 
              getToolkit().beep(); 
              evt.consume();      
          }
         
    }//GEN-LAST:event_txtNumPerKeyTyped

    private void txtCantSinIvaKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtCantSinIvaKeyTyped
        // TODO add your handling code here:
          char c=evt.getKeyChar(); 
          if(!Character.isDigit(c)) 
          { 
              getToolkit().beep(); 
              evt.consume();      
          }
         
          if(!Character.isLetter(c)) 
          { 
              getToolkit().beep(); 
              evt.consume(); 
          }
    }//GEN-LAST:event_txtCantSinIvaKeyTyped

